import "./MainCompStyles.css"
import { useState, useEffect } from "react";
import axios from "axios"
import MyHook from "../App"

const MainFrame = () => {

    const [x, setX] = useState([])

    function ReqHandler(e) {

        e.preventDefault()

        useEffect(()=>{
        const article = { title: e.target[0].value };
        axios.post('/handle_data', article)
        .then(function (response) {
        })

        axios.get("/handle_data")
        .then((response) => {

            console.log("output: ")
            console.log(response.data)

            setX(response.data)
        })

        }, [x]);

    }

    useEffect(() => {
        console.log('the age has changed', x)
     }, [x])


    const conversations = Array.from(x)

    console.log("conversation", x)

    return(

        <div>
            <div className="MainFrame">

                <form onSubmit={ReqHandler}>
                    <label>Hello there?</label>
                    <input type="text" name="user_input" className="user_in"/>
                    <input type="submit" name="user_sub" className="user_in"/>
                </form>
            </div>
            <div>

                    {conversations.map((conversation) => (

                        <div className="displayChat" key={conversation.id}>
                            <p>{conversation.you}</p>
                            <p>{conversation.Chatbot}</p>
                        </div>
                    ))}

            </div>
        </div>

    )


}

export default MainFrame;
